import "../styles/HeaderSong.css"
import { useContext} from 'react'
import { MusicContext } from './MusicContext'
import { database } from '../firebase-config'
import { ref, onValue } from 'firebase/database'

function HeaderSong(){

    const {setMainSong} = useContext(MusicContext)
    var getMusicData = ref(database, 'SongInformation/' +'Songs/');
    const HandleClick = (current) => {
        const Name = document.getElementById(current).textContent;
        onValue(getMusicData, (snapshot) => {
            snapshot.forEach(childSnapshot => {
                let data = childSnapshot.val();
                if (Name === data.songName)  {
                    setMainSong (data.song, data.imgSrc, data.songName, data.artist)
                }          
            })
        })
    }

    return (
        <div id='HeaderSongContainer'>
         <h2 id="header-context">Good morning</h2>
         <div className='SuggestedSong'>
            <div className="header-suggested song1" onClick={() => HandleClick('s1')}  >
                <img src="https://i.pinimg.com/236x/b5/aa/fd/b5aafd413f8733b67daf6b04c55e4057.jpg" alt="Lối nhỏ" class="song-img" />
                <p className="song-name" id='s1'>Lối Nhỏ</p>
            </div>

            <div className="header-suggested song2" onClick={() => HandleClick('s2')} >
                <img src="https://i.pinimg.com/236x/ab/da/09/abda09eb723d24a5057339fe9b6bf47b.jpg" alt="Hotel cali" class="song-img"/>
                <p className="song-name" id='s2'>Hotel California</p>
            </div>

            <div className="header-suggested song3" onClick={() => HandleClick('s3')} >
                <img src="data:image/jpeg;base64," alt="LLHP" class="song-img"/>
                <p class="song-name" id='s3'>Lọ Lem Hè Phố</p>
            </div>

            <div className="header-suggested song4" onClick={() => HandleClick('s4')} >
                <img src="data:image/jpeg;base64," alt="Thanh xuân" class="song-img"/>
                <p class="song-name" id='s4'>Attention</p>
            </div>

            <div className="header-suggested song5" onClick={() => HandleClick('s5')} >
                <img src="data:image/jpeg;base64," alt="VìAnhĐâuCóBiết" class="song-img"/>
                <p class="song-name" id='s5'>Vì Anh Đâu Có Biết</p>
            </div>

            <div className="header-suggested song6" onClick={() => HandleClick('s6')} >
                <img src="https://i.pinimg.com/236x/a9/b1/79/a9b17942176fad02e453224062d52515.jpg" alt="Havana" class="song-img"/>
                <p className="song-name" id='s6'>Havana</p>
            </div> 
            {/*Header song: end*/} 
         </div> 
        </div> 
    )
}
export {HeaderSong}